import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    // Simple test to see if React is working
    console.log('React App mounted successfully!');
    
    // Test SSE connection
    const eventSource = new EventSource('/stream');
    
    eventSource.addEventListener('hello', () => {
      console.log('SSE connected!');
      setIsConnected(true);
    });

    eventSource.onerror = (error) => {
      console.log('SSE error:', error);
      setIsConnected(false);
    };

    return () => eventSource.close();
  }, []);

  return (
    <div className="App">
      <div className="test-container">
        <h1>üêã WhaleWatch 3D - Test Mode</h1>
        <p>If you can see this, React is working!</p>
        <div className={`connection-status ${isConnected ? 'connected' : 'disconnected'}`}>
          {isConnected ? 'üü¢ Connected to Backend' : 'üî¥ Disconnected - Demo Mode'}
        </div>
        <div className="test-info">
          <h3>Debug Information:</h3>
          <ul>
            <li>React App: ‚úÖ Loaded</li>
            <li>Backend Connection: {isConnected ? '‚úÖ Connected' : '‚ùå Not Connected'}</li>
            <li>3D Components: üîÑ Loading...</li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default App;
