import React, { useState } from 'react';
import { motion } from 'framer-motion';
import './Controls.css';

const Controls = ({ config, onUpdateConfig }) => {
  const [localConfig, setLocalConfig] = useState(config);
  const [isUpdating, setIsUpdating] = useState(false);

  const handleInputChange = (field, value) => {
    setLocalConfig(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleApply = async () => {
    setIsUpdating(true);
    try {
      await onUpdateConfig(localConfig);
    } catch (error) {
      console.error('Failed to update config:', error);
    } finally {
      setIsUpdating(false);
    }
  };

  const handleReset = () => {
    setLocalConfig(config);
  };

  return (
    <motion.div 
      className="controls-card"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <h3>‚öôÔ∏è Settings</h3>
      
      <div className="control-group">
        <label htmlFor="threshold">
          Whale Threshold (BTC)
          <span className="tooltip">Minimum BTC amount to classify as a whale transaction</span>
        </label>
        <div className="input-wrapper">
          <input
            id="threshold"
            type="number"
            min="0.1"
            step="0.1"
            value={localConfig.threshold}
            onChange={(e) => handleInputChange('threshold', parseFloat(e.target.value) || 0)}
            className="control-input"
          />
          <span className="unit">BTC</span>
        </div>
      </div>

      <div className="control-group">
        <label htmlFor="interval">
          Summary Interval (seconds)
          <span className="tooltip">How often to generate summary statistics</span>
        </label>
        <div className="input-wrapper">
          <input
            id="interval"
            type="number"
            min="5"
            step="1"
            value={localConfig.interval}
            onChange={(e) => handleInputChange('interval', parseInt(e.target.value) || 0)}
            className="control-input"
          />
          <span className="unit">sec</span>
        </div>
      </div>

      <div className="control-actions">
        <motion.button
          className="apply-btn"
          onClick={handleApply}
          disabled={isUpdating}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          {isUpdating ? 'üîÑ Updating...' : '‚úÖ Apply'}
        </motion.button>
        
        <motion.button
          className="reset-btn"
          onClick={handleReset}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          üîÑ Reset
        </motion.button>
      </div>

      <div className="current-status">
        <div className="status-item">
          <span className="status-label">Current Threshold:</span>
          <span className="status-value">{config.threshold} BTC</span>
        </div>
        <div className="status-item">
          <span className="status-label">Current Interval:</span>
          <span className="status-value">{config.interval}s</span>
        </div>
      </div>
    </motion.div>
  );
};

export default Controls;
